package se.dandel.recipe.web;import java.util.HashMap;import java.util.Map;public class Types {    private static Map<String, String> TYPES = new HashMap<>();    private static Map<String, String> REVERSE_TYPES = new HashMap<>();    static {        register("FISH", "Fisk");        register("ITRIM_LCD", "Itrim LCD");        register("MEAT", "Kött");        register("POULTRY", "Fågel");        register("SIDE_ORDER_GREEN", "Grönsaker");        register("SIDE_ORDER_ROOT", "Rotfrukter");        register("VEGGIE", "Vegetariskt");    }    private static void register(String key, String value) {        TYPES.put(key, value);        REVERSE_TYPES.put(value, key);    }    public static String get(String key) {        return TYPES.get(key);    }    public static String lookup(String type) {        return REVERSE_TYPES.get(type);    }}